mapper.update:
update subject_area set name = #{name},
  theme_num = #{theme_num},
  post_num = #{post_num},
  last_post_time = #{last_post_time}
where subject_id = #{subject_id}

update post set content = #{content},
  time = #{time},
  type = #{type},
  status = #{status}
where post_id = #{post_id}
